SELECT RN, p_no, categ_cd, p_name, p_px, p_dc, p_mfr, p_dtl, p_up_folder, p_img, p_amt, p_purchasable, register_date, modify_date FROM ( SELECT /*+INDEX_DESC(p_tbl pk_p_no) */ ROWNUM RN, p_no, categ_cd, p_name, p_px, p_dc, p_mfr, p_dtl, p_up_folder, p_img, p_amt, p_purchasable, register_date, modify_date FROM p_tbl WHERE
categ_cd = #{categ_cd} and p_purchasable = 'Y' and
ROWNUM <= #{cri.pageNum} * #{cri.amount} ) WHERE RN > (#{cri.pageNum}-1) * #{cri.amount}
